package com.example.wallpaperapp;

import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Toast;
import androidx.appcompat.app.AppCompatActivity;
import java.util.Random;

public class RegistrationActivity extends AppCompatActivity {

    private EditText editTextAccount, editTextPassword, editTextVerificationCode;
    private Button buttonRegister;
    private String verificationCode;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_registration);

        editTextAccount = findViewById(R.id.editTextAccount);
        editTextPassword = findViewById(R.id.editTextPassword);
        editTextVerificationCode = findViewById(R.id.editTextVerificationCode);
        buttonRegister = findViewById(R.id.buttonRegister);

        generateVerificationCode();

        buttonRegister.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                String inputCode = editTextVerificationCode.getText().toString();
                if (inputCode.equals(verificationCode)) {
                    Toast.makeText(RegistrationActivity.this, "Registration Successful", Toast.LENGTH_SHORT).show();
                    finish(); // Return to LoginActivity
                } else {
                    Toast.makeText(RegistrationActivity.this, "Verification Code Incorrect", Toast.LENGTH_SHORT).show();
                }
            }
        });
    }

    private void generateVerificationCode() {
        Random random = new Random();
        verificationCode = String.valueOf(random.nextInt(9000) + 1000); // 4-digit code
        Toast.makeText(this, "Verification Code: " + verificationCode, Toast.LENGTH_SHORT).show();
    }
}
