import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Toast;
import androidx.appcompat.app.AppCompatActivity;

import java.util.Random;

public class RegistrationActivity extends AppCompatActivity {

    private EditText accountEditText;
    private EditText passwordEditText;
    private EditText verificationCodeEditText;
    private Button registerButton;
    private String generatedCode;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_registration);

        accountEditText = findViewById(R.id.account);
        passwordEditText = findViewById(R.id.password);
        verificationCodeEditText = findViewById(R.id.verification_code);
        registerButton = findViewById(R.id.register_button);

        generatedCode = generateRandomCode();

        registerButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                String userInputCode = verificationCodeEditText.getText().toString();
                if (userInputCode.equals(generatedCode)) {
                    // Display success message and navigate back to login
                    Toast.makeText(RegistrationActivity.this, "Registration Successful", Toast.LENGTH_SHORT).show();
                    finish(); // Return to login page
                } else {
                    // Display failure message
                    Toast.makeText(RegistrationActivity.this, "Verification Code Incorrect", Toast.LENGTH_SHORT).show();
                }
            }
        });
    }

    private String generateRandomCode() {
        Random random = new Random();
        return String.valueOf(random.nextInt(9999 - 1000) + 1000); // Generate a 4-digit code
    }
}
