### 3. RegistrationActivity.java
```java
package com.example.wallpaper;

import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Toast;
import androidx.appcompat.app.AppCompatActivity;

import java.util.Random;

public class RegistrationActivity extends AppCompatActivity {
    private EditText accountInput, passwordInput, verificationCodeInput;
    private String generatedCode;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_registration);

        accountInput = findViewById(R.id.account);
        passwordInput = findViewById(R.id.password);
        verificationCodeInput = findViewById(R.id.verification_code);
        Button registerButton = findViewById(R.id.register);

        generatedCode = generateRandomCode();

        registerButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                if (verificationCodeInput.getText().toString().equals(generatedCode)) {
                    Toast.makeText(RegistrationActivity.this, "Registration Successful", Toast.LENGTH_SHORT).show();
                    // Navigate back to login page
                    finish();
                } else {
                    Toast.makeText(RegistrationActivity.this, "Verification Code Incorrect", Toast.LENGTH_SHORT).show();
                }
            }
        });
    }

    private String generateRandomCode() {
        Random random = new Random();
        return String.valueOf(random.nextInt(9999 - 1000) + 1000); // 4-digit code
    }
}
